mapper.update:
UPDATE FRANCHISE_T F
SET F.FRN_TEL = #{frnTel},
    F.MODIFY_DT = CURRENT_DATE,
    F.MODIFIER = #{modifier}
WHERE EXISTS (
    SELECT POS_NO
    FROM EMPLOYEE_T E
    WHERE E.FRN_NO = F.FRN_NO
      AND E.POS_NO = 0
)  AND F.FRN_NO = #{frnNo}

UPDATE FRANCHISE_T F
SET F.FRN_OWNER = #{frnOwner},
    F.MODIFY_DT = CURRENT_DATE,
    F.MODIFIER = ${modifier}
WHERE EXISTS (
    SELECT E.FRN_NO
    FROM EMPLOYEE_T E
    WHERE E.FRN_NO = F.FRN_NO
)  AND F.FRN_NO = ${frnNo}

UPDATE FRANCHISE_T F


        F.FRN_START_TIME = #{frnStartTime},


        F.FRN_END_TIME = #{frnEndTime},

    F.MODIFY_DT = CURRENT_DATE,
    F.MODIFIER = ${modifier}

WHERE EXISTS (
    SELECT E.FRN_NO
    FROM EMPLOYEE_T E
    WHERE E.FRN_NO = F.FRN_NO
)  AND F.FRN_NO = ${frnNo}
